import React from "react";

import images from "../assets/images/images.js"

//COMPONENTS
import Header from "../components/Header";
import Section from "../components/Section";
import PersonBlock from "../components/PersonBlock";
import FilterButton from "../components/FilterButton";

export default class Team extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        leads: [nadine, nadine, nadine, nadine, nadine],
        team: [nadine, nadine, nadine, nadine, nading, nadine, nadine, nadine, nading, nadine, nadine, nadine,],
        filter: "All",
        all_filters: ["All", "Business", "Design", "Software", "Electrical", "Integrative Design", "Research"]
      };
    }
  render() {
    //notation passes along all props from the Home component to child components
    return (
        <div className="home-container">
          <Header photo={images.group} state={this.state}/>
          <div className="sections-container">
              <div className = "leads-container">
                <div className = "team-title">Leads</div>
                <div className = "lead-blocks-container">
                    {this.state.leads.map((person, index) => {
                        if (person!= null){
                            return (<PersonBlock data = {person} /> )}})}
                </div>
              </div>
              <div className = "team-container">
                <div className = "team-title">Team</div>
                <div className = "team-filters-container">
                    {this.state.all_filters.map((filter, index) => {
                        if (filter!= null){
                            return (<FilterButton title = {filter} selected = {this.state.filter === filter} updateFilter = {() => {this.setState({filter: filter})}}/> )}})}
                </div>
                <div className = "team-blocks-container">
                    {this.state.team.map((person, index) => {
                        if (person!= null){
                            return (<PersonBlock data = {person} /> )}})}
                </div>
              </div>
          </div>
        </div>
    );
  }
}
var nadine = {name: 'Nadine Farhat', title: 'President', year: 'Sophomore', major: 'Computer Science', projects: 'Menstrual Game, INDAGO'}
