import React from "react";

export default class PersonBlock extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        flipped : false
      };
    }
  render() {
    var person = this.props.data
    if (!this.state.flipped){
        return (
          <div className= "person-block-front"onClick={() => {this.setState({flipped: !this.state.flipped})}}>
            <div className = "person-photo">
            </div>
            <div>{person.name}</div>
            <div>{person.title}</div>
          </div>
                );
    }else{
      return (
          <div className= "person-block-back" onClick={() => {this.setState({flipped: !this.state.flipped})}}>
            <div>{person.name}</div>
            <div>{person.title}</div>
            <div>Year</div>
            <div>{person.year}</div>
            <div>Major</div>
            <div>{person.major}</div>
            <div>Team Projects</div>
            <div>{person.projects}</div>
          </div>
                );

  }
  }
}
