import React from "react";

export default class FilterButton extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        selected: this.props.selected
      };
    }
  render() {
    let classN = "filter-button"
    let underline = <div/>
    if (this.state.selected){
        classN = classN + " selected"
        underline = <div className = "filter-underline"/>
    }
    return (
        <div className= "filter-button" onClick={() => {this.setState({selected: !this.state.selected}); this.props.updateFilter(this.props.title)}}>
          <div className = {classN}>{this.props.title}</div>
          {underline}
          </div>
                );

  }
  }
